mapper.select:
select count(*) from `api_req` ar left join `client` c on ar.`client_id` = c.`id`


        (c.`corp_name` like '%${keyword}%' or c.`short_name` like '%${keyword}%')

    and ar.`product_id` = #{productId}
    and ar.`bill_plan` = #{billPlan}
    and ar.`create_time` >= #{fromDate}
    and ar.`create_time` < #{toDate}

select sum(ar.`fee`) from `api_req` ar left join `client` c on ar.`client_id` = c.`id`


        (c.`corp_name` like '%${keyword}%' or c.`short_name` like '%${keyword}%')

    and ar.`product_id` = #{productId}
    and ar.`bill_plan` = #{billPlan}
    and ar.`create_time` >= #{fromDate}
    and ar.`create_time` < #{toDate}

select count(ar.`id`) from `api_req` ar left join `client` c on ar.`client_id` = c.`id` and ar.`hit` = 0


        (c.`corp_name` like '%${keyword}%' or c.`short_name` like '%${keyword}%')

    and ar.`product_id` = #{productId}
    and ar.`bill_plan` = #{billPlan}
    and ar.`create_time` >= #{fromDate}
    and ar.`create_time` < #{toDate}

select ar.`create_time` as create_time, ar.`request_no` as request_no, c.`corp_name` as corp_name,
c.`short_name` as short_name, cu.`username` as username, p.`name` as product_name, ar.`bill_plan` as bill_plan,
ar.`hit` as hit, ar.`fee` as fee, ar.`balance` as balance, ar.`cost` as cost from `api_req` ar left join
`client` c on ar.`client_id` = c.`id` left join `client_user` cu on ar.`user_id` = cu.`id` left join `product` p
on ar.`product_id` = p.`id`


        (c.`corp_name` like '%${keyword}%' or c.`short_name` like '%${keyword}%')

    and ar.`product_id` = #{productId}
    and ar.`bill_plan` = #{billPlan}
    and ar.`create_time` >= #{fromDate}
    and ar.`create_time` < #{toDate}

order by ar.`create_time` desc

select ar.`id` as id, ar.`create_time` as create_time, ar.`update_time` as update_time, ar.`user_id` as user_id,
ar.`client_id` as client_id, ar.`product_id` as product_id, ar.`hit` as hit, ar.`request_no` as request_no,
ar.`request_ip` as request_ip, ar.`bill_plan` as bill_plan, ar.`fee` as fee, ar.`balance` as balance, ar.`cost`
as cost, c.`corp_name` as corp_name, c.`short_name` as short_name, cu.`username` as username, p.`name` as
product_name from `api_req` ar left join `client` c on ar.`client_id` = c.`id` left join `client_user` cu on
c.`primary_user_id` = cu.`id` left join `product` p on ar.`product_id` = p.`id`

    ar.`client_id` = #{clientId}
    and ar.`user_id` = #{userId}
    and ar.`product_id` = #{productId}
    and ar.`create_time` >= #{startDate}
    and ar.`create_time` <= #{endDate}

order by ar.`create_time` desc

